package pattern.problems;

public class OddEvenPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 7;
		
		for(int i=1; i<=n; i++) {
			
			if(i%2==0)
				System.out.print("#");
			else
				System.out.print("@");
				
		}

	}

}
